package main.calculator;import main.calculator.controller.CalculatorController;import main.calculator.engine.Calculator;import main.calculator.repository.LocalStorage;import main.calculator.repository.Storage;import main.calculator.service.CalculatorService;import main.common.io.InputConsole;import main.common.io.OutputConsole;public class MainApplication {    public static void main(String[] args) {        InputConsole ic = new InputConsole();        OutputConsole oc = new OutputConsole();        Storage storage = new LocalStorage();        Calculator calculatorEngine = new Calculator();        CalculatorService calculator = new CalculatorService(storage, calculatorEngine);        CalculatorController calculatorController = new CalculatorController(ic, oc, calculator);        calculatorController.run();    }}