package main.calculator.model;import main.common.exception.ExpressionEmptyException;import main.common.exception.ExpressionFormatException;import java.util.regex.Pattern;// 클래스명 변경public class CalculateExpression {    private static final String VALID_OPERATION_PATTERN = "^[+\\-*/\\d()]*$";    private final String expression;    public CalculateExpression(String expression) {        expression = removeSpace(expression);        validateExpression(expression);        this.expression = expression;    }    private void validateExpression(String expression) {        if (expression.isEmpty()) {            throw new ExpressionEmptyException();        }        if (!Pattern.matches(VALID_OPERATION_PATTERN, expression)) {            throw new ExpressionFormatException();        }    }    public String getExpression() {        return expression;    }    // 공백 제거    private String removeSpace(String expression) {        return expression.replaceAll(" ", "");    }}