package main.calculator.repository;import main.calculator.model.Result;import java.util.ArrayList;import java.util.List;import java.util.stream.Collectors;public class LocalStorage implements Storage {    private final List<Result> history = new ArrayList<>();    @Override    public void save(Result result) {        history.add(result);    }    @Override    public List<Result> findAll() {        return history.stream().collect(Collectors.toList());    }}