package main.calculator.service;import main.calculator.engine.Calculator;import main.calculator.model.RequestExpression;import main.calculator.model.Result;import main.calculator.repository.Storage;import java.util.List;public class CalculatorService {    private final Storage storage;    private final Calculator calculator;    public CalculatorService(Storage storage, Calculator calculator) {        this.storage = storage;        this.calculator = calculator;    }    public Result calculate(RequestExpression userRequest) {        double answer = calculator.calculate(userRequest);        Result result = new Result(userRequest, answer);        storage.save(result);        return result;    }    public List<Result> getHistory() {        return storage.findAll();    }}