package main.calculator.util;import main.common.exception.ErrorMessage;import java.util.Arrays;public enum Menu {    HISTORY("1", "조회"),    CALCULATE("2", "계산"),    EXIT("3", "종료");    private final String option;    private final String description;    Menu(String option, String description) {        this.option = option;        this.description = description;    }    public static Menu from(String inputMenu) {        return Arrays.stream(Menu.values())                .filter(menu -> menu.option.equals(inputMenu))                .findAny()                .orElseThrow(() -> new IllegalArgumentException(ErrorMessage.INVALID_MENU_OPTION.getmessage()));    }}