package main.calculator.validate;import main.common.exception.ErrorMessage;import java.util.regex.Pattern;public class ExpressionValidator {    private static final String VALID_OPERATION_PATTERN = "^[+\\-*/\\d()]*$";    public static boolean validateExpression(String expression) {        String expressionWithoutSpace = removeSpace(expression);        if (expressionWithoutSpace.isEmpty()) {            throw new IllegalArgumentException(ErrorMessage.EMPTY_EXPRESSION.getmessage());        }        if (!Pattern.matches(VALID_OPERATION_PATTERN, expressionWithoutSpace)) {            throw new IllegalArgumentException(ErrorMessage.WRONG_EXPRESSION_FORMAT.getmessage());        }        return true;    }    private static String removeSpace(String expression) {        return expression.replaceAll(" ", "");    }}