package main.common.io;import main.calculator.model.Result;import java.util.List;public class OutputConsole {    private static final String MENU_MESSAGE = """            1. 선택            2. 계산            3. 종료            """;    private static final String SELECT_MESSAGE = "\n 선택 : ";    public void printMenu() {        System.out.println(MENU_MESSAGE);            System.out.print(SELECT_MESSAGE);    }    public void printResult(Result result) {        System.out.println(result.result());    }    public void printHistory(List<Result> history) {        history.forEach(result -> {            System.out.println(result.expression().expression() + " = " + result.result());        });    }}