package main.common.io;import main.calculator.util.Menu;import main.calculator.model.Result;import java.util.Arrays;import java.util.List;public class OutputConsole {    private static final String MENU_MESSASGE = """            1. 선택            2. 계산            3. 종료            """;    private static final String SELECT_MESSAGE = "";    public void printMenu() {        Arrays.stream(Menu.values())                .forEach(menu -> System.out.println(menu.getOption() + ". " + menu.getDescription()));        System.out.print("\n 선택 : ");    }    public void printResult(Result result) {        System.out.println(result.result() + "\n");    }    public void printHistory(List<Result> history) {        history.forEach(result -> {            System.out.println(result.getResult());        });    }}